import java.util.ArrayList;

public class B5_0{
	public static void main(String[] args) {
	    ArrayList<Dice> arrayList = new ArrayList<Dice>();
	    arrayList.add(new Dice());
	    arrayList.add(new Dice());
	    arrayList.add(new Dice());

	    for(int i = 0;i < arrayList.size();i++){
	        Dice arrayList = arrayList.get(i);
	        arrayList.cast();
	        System.out.println(arrayList.getValue);
	    }
	}
}

