import java.util.ArrayList;

public class B5_0{
	public static void main(String[] args) {
	    ArrayList<Dice> arrayList = new ArrayList<Dice>();
	    for(int i = 0; i < 3; i++){
	        arrayList.add(new Dice());   
	    }

	    for(int i = 0;i < arrayList.size();i++){
	        Dice dice = arrayList.get(i);
	        dice.cast();
	        System.out.println(dice.getValue());
	    }
	}
}
